    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
                String cancion=JOptionPane.showInputDialog(this, "Ingrese el nombre de la cancion: "); 
                String album=JOptionPane.showInputDialog(this, "Ingrese el nombre del album: ");
                String artist=JOptionPane.showInputDialog(this, "Ingrese el nombre del artista: ");
                int year=Integer.parseInt(JOptionPane.showInputDialog(this, "Ingrese el año de lanzamiento: "));
                int secs=Integer.parseInt(JOptionPane.showInputDialog(this, "Ingrese la duracion de cancion en segundos: "));
                
                songs.add(new Cancion(cancion, album, artist, year, secs));
                
                


// TODO add your handling code here:
    }//GEN-LAST:event_jButton2MouseClicked

