    private void jButton6MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton6MouseClicked
        int index=-1;
        index=Integer.parseInt(JOptionPane.showInputDialog(this,getlist()+"\n "
                + "Ingrese el indice de la cancion a modificar: "))-1;
        jModificar.setVisible(true);
        jModificar.pack();
        jModificar.setLocationRelativeTo(this);
        
        if(index >=0 && index<songs.size()){
            indexselect=index;
            
            jName.setText(songs.get(indexselect).getName());
            jAlbum.setText(songs.get(indexselect).getAlbum());
            jArtist.setText(songs.get(indexselect).getArtist());
            jYear.setText(Integer.toString(songs.get(indexselect).getYear()));
            jLength.setText(Integer.toString(songs.get(indexselect).getSecs()));
            
            
        }
        
        
        
        
        
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton6MouseClicked

    private void jButton5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton5MouseClicked
        int index=Integer.parseInt(JOptionPane.showInputDialog(this, getlist()+"\n"
                + "Ingrese el indice de cancion a mostrar info: "))-1;
        
        
        
        JOptionPane.showMessageDialog(null, "La informacion de esta cancion es: \n"
                + "Nombre: "+songs.get(index).getName()+"\n"
                        + "Album: "+songs.get(index).getAlbum()+"\n"
                                + "Artista: "+songs.get(index).getArtist()+"\n"
                                        + "Año: "+Integer.toString(songs.get(index).getYear())+"\n"
                                                + "Duracion: "+Integer.toString(songs.get(index).getSecs()));
        
        // TODO add your handling code here:
    }//GEN-LAST:event_jButton5MouseClicked

